import React, { useState, useEffect } from 'react';
import PaymentForm from '../components/PaymentForm';

interface Transaction {
  id: string;
  recipient: string;
  amount: number;
  method: string;
  date: string;
}

const Dashboard: React.FC = () => {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await fetch(
          process.env.REACT_APP_API_BASE_URL ||
            'https://ziagg50mj8.execute-api.us-east-1.amazonaws.com/default/transactionsHandler',
        );

        if (!response.ok) {
          throw new Error('Failed to fetch transactions');
        }

        const data = await response.json();
        setTransactions(data);
      } catch (err) {
        console.error('Error fetching transactions:', err);
        setError('Error fetching transactions');
      }
    };

    fetchTransactions();
  }, []);

  const onAddTransaction = async (transaction: Transaction) => {
    try {
      const response = await fetch(
        process.env.REACT_APP_API_BASE_URL ||
          'https://ziagg50mj8.execute-api.us-east-1.amazonaws.com/default/transactionsHandler',
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(transaction),
        },
      );

      if (!response.ok) {
        throw new Error('Failed to add transaction');
      }

      const newTransaction = await response.json();
      setTransactions((prevTransactions) => [...prevTransactions, newTransaction]);
    } catch (err) {
      console.error('Error adding transaction:', err);
      setError('Error adding transaction');
    }
  };

  return (
    <div className="dashboard">
      <h1 className="dashboard-title">Dashboard</h1>
      <div className="dashboard-content">
        <div className="dashboard-section">
          <h2 className="dashboard-subtitle">Add a Transaction</h2>
          <PaymentForm onAddTransaction={onAddTransaction} />
        </div>
        <div className="dashboard-section">
          <h2 className="dashboard-subtitle">Transaction History</h2>
          {error && <p className="text-red-600">{error}</p>}
          <table className="w-full border-collapse border border-gray-300">
            <thead>
              <tr>
                <th className="border border-gray-300 p-2">Recipient</th>
                <th className="border border-gray-300 p-2">Amount</th>
                <th className="border border-gray-300 p-2">Method</th>
                <th className="border border-gray-300 p-2">Date</th>
              </tr>
            </thead>
            <tbody>
              {transactions.map((transaction) => (
                <tr key={transaction.id}>
                  <td className="border border-gray-300 p-2">{transaction.recipient}</td>
                  <td className="border border-gray-300 p-2">{transaction.amount}</td>
                  <td className="border border-gray-300 p-2">{transaction.method}</td>
                  <td className="border border-gray-300 p-2">{new Date(transaction.date).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
