import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { auth } from "../firebase";
import TransactionTable from "../components/TransactionTable";
import PaymentForm from "../components/PaymentForm";

const Dashboard = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  // Fetch transactions
  useEffect(() => {
    const fetchTransactions = async () => {
      setLoading(true);
      setError("");
      try {
        const response = await fetch(
          process.env.REACT_APP_API_BASE_URL ||
            "https://ziagg50mj8.execute-api.us-east-1.amazonaws.com/default/transactionsHandler"
        );

        if (!response.ok) {
          throw new Error("Failed to fetch transactions");
        }

        const data = await response.json();
        setTransactions(data);
      } catch (error) {
        setError("Error fetching transactions");
        console.error("Error fetching transactions:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchTransactions();
  }, []);

  // Add a new transaction
  const onAddTransaction = async (transaction) => {
    try {
      const response = await fetch(
        process.env.REACT_APP_API_BASE_URL ||
          "https://ziagg50mj8.execute-api.us-east-1.amazonaws.com/default/transactionsHandler",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(transaction),
        }
      );

      if (!response.ok) {
        throw new Error("Failed to add transaction");
      }

      const newTransaction = await response.json();
      setTransactions((prevTransactions) => [...prevTransactions, newTransaction]);
    } catch (error) {
      console.error("Error adding transaction:", error);
      setError("Error adding transaction");
    }
  };

  const handleLogout = () => {
    auth.signOut();
    navigate("/login");
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1 className="dashboard-title">Dashboard</h1>
        <button className="dashboard-logout-button" onClick={handleLogout}>
          Logout
        </button>
      </div>
      <div className="dashboard-content">
        <div className="dashboard-section">
          <h2 className="dashboard-subtitle">Add a Transaction</h2>
          <PaymentForm onAddTransaction={onAddTransaction} />
        </div>
        <div className="dashboard-section">
          <h2 className="dashboard-subtitle">Transaction History</h2>
          {loading ? (
            <p>Loading...</p>
          ) : error ? (
            <p className="error-message">{error}</p>
          ) : (
            <TransactionTable transactions={transactions} loading={false} error={""} />
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
