import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { auth } from "../firebase";
import TransactionTable from "../components/TransactionTable";
import PaymentForm from "../components/PaymentForm";

const Dashboard = () => {
  const [transactions, setTransactions] = useState([]);
  const navigate = useNavigate();

  // Fetch transactions
  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await fetch(
          process.env.REACT_APP_API_BASE_URL || "https://ziagg50mj8.execute-api.us-east-1.amazonaws.com/default/transactionsHandler"
        );
        const data = await response.json();
        setTransactions(data);
      } catch (error) {
        console.error("Error fetching transactions:", error);
      }
    };

    fetchTransactions();
  }, []);

  const handleLogout = () => {
    auth.signOut();
    navigate("/login");
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1 className="dashboard-title">Dashboard</h1>
        <button className="dashboard-logout-button" onClick={handleLogout}>
          Logout
        </button>
      </div>
      <div className="dashboard-content">
        <div className="dashboard-section">
          <h2 className="dashboard-subtitle">Add a Transaction</h2>
          <PaymentForm onAddTransaction={function (transaction: { recipient: string; amount: number; method: string; }): void {
                      throw new Error("Function not implemented.");
                  } } />
        </div>
        <div className="dashboard-section">
          <h2 className="dashboard-subtitle">Transaction History</h2>
          <TransactionTable transactions={transactions} loading={false} error={""} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
